mapper.select:
	
	select t.*
	from(
		select rownum rn, a.*
		from(
			select p.seq,p.age,p.kind,p.weight,p.location,b.subject
			from board b, pparcelboard p
			where b.seq = p.seq
			order by b.seq desc
				) a
		where rownum <= #{end}
		) t
	where t.rn > #{start}

	
	select t.*
	from(
		select rownum rn, a.*
		from(
			select m.kind,m.weight,m.age,m.feature
			from board b, missingboard m
			where b.seq = m.seq
			order by b.seq desc
				) a
		where rownum <= #{end}
		) t
	where t.rn > #{start}


select t.*
from(
	select rownum rn, a.*
	from(
		select p.seq,p.age,p.kind,p.weight,p.location,b.subject
		from board b, pparcelboard p
		where b.seq = p.seq
		and email='${email}'
		order by b.seq desc
			) a
	where rownum <= #{end}
	) t
where t.rn > #{start}
