mapper.select:
   
   select t.*
   from(
      select rownum rn, a.*
      from(
         select p.seq,p.age,p.kind,p.weight,p.location,b.subject,b.content
         from board b, pparcelboard p
         where b.seq = p.seq
         order by b.seq desc
            ) a
      where rownum <= #{end}
      ) t
   where t.rn > #{start}

   select t.*
   from(
      select rownum rn, a.*
      from(
         select b.seq,b.subject,b.content,d.centername,d.gatheringfee,d.cdonationfee,d.cdonationpurpose
         from board b,donationboard d
         where b.seq=d.seq 
         order by b.seq desc
         ) a
      where rownum <= #{end}
      ) t
   where t.rn > #{start}

select t.*
from(
   select rownum rn, a.*
   from(
      select b.seq,b.subject,b.content,v.tel,v.centername,v.volunteerkind,v.volunteerdate,v.centerlocation
      from board b,volunteerboard v
      where b.seq=v.seq 
      ) a
   where rownum <= #{end}
   ) t
where t.rn > #{start}

select t.*
from(
   select rownum rn, a.*
   from(
      select a.seq,a.writeremail,a.applyemail,a.subject,m.tel,m.addr1
      from applyboard a,member m
      where a.writeremail='kjhabc2002@naver.com' and a.applyemail=m.email
   ) a
   where rownum <= #{end}
   ) t
where t.rn > #{start}

   select t.*
   from(
      select rownum rn, a.*
      from(
         select b.seq,b.content,p.age,p.kind,p.weight,p.location,b.subject
         from board b,pparcelboard p
         where b.seq=p.seq 
      ) a
      where rownum <= #{end}
      ) t
   where t.rn > #{start}


select t.*
from(
   select rownum rn, a.*
   from(
      select p.seq,p.age,p.kind,p.weight,p.location,b.subject
      from board b, pparcelboard p
      where b.seq = p.seq
      and email='${email}'
      order by b.seq desc
         ) a
   where rownum <= #{end}
   ) t
where t.rn > #{start}

   select t.*
   from(
      select rownum rn, a.*
      from(
         select b.seq,b.subject,b.content,d.centername,d.gatheringfee,d.cdonationfee,d.cdonationpurpose
         from board b,donationboard d
         where b.seq=d.seq 
         and b.email='${email}'
         ) a
      where rownum <= #{end}
      ) t
   where t.rn > #{start}

select t.*
from(
   select rownum rn, a.*
   from(
      select b.seq,b.content,b.subject,v.tel,v.centername,v.volunteerkind,v.volunteerdate,v.centerlocation
      from board b,volunteerboard v
      where b.seq=v.seq 
      and b.email='${email}'
   ) a
   where rownum <= #{end}
   ) t
where t.rn > #{start}

   select t.*
   from(
      select rownum rn, a.*
      from(
         select b.seq,b.subject,b.content,p.age,p.kind,p.weight,p.location
         from board b,pparcelboard p
         where b.seq=p.seq 
         and b.seq in(select seq from parcellist where email='${email}')
   ) a
      where rownum <= #{end}
      ) t
   where t.rn > #{start}

   select t.*
   from(
      select rownum rn, a.*
      from(
         select b.seq,b.subject,b.content,p.age,p.kind,p.weight,p.location
         from board b,pparcelboard p
         where b.seq=p.seq 
         and b.seq in(select seq from picklist where email='${email}')
   ) a
      where rownum <= #{end}
      ) t
   where t.rn > #{start}
